package com.syntax.class01;

public class PracticeVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte  num1 = 10;
		// From -128 to 127
		
		short num2 = 100;
		// From -32768 to 32767

		int num3 = 1000;
		//From -2147483648 to 2147483647

		long num4 = 10000;
		// From -9,223,372,036,854,775,808 to 9,223,372,036,854,775,807

		
		float num5 = 100f;
		// If number is too long we have to add f at the end.

		double num6 = 10.10;
		// we can use any double short or long number.

		char Hashtag = '#';
		// All unicode  charecters

		boolean IamDoingJavaAssignment = true;
		//It just store True and False

		
		System.out.print("byte = ");
		System.out.println(num1);
		
		System.out.print("short = ");
		System.out.println(num2);
		
		System.out.print("int = ");
		System.out.println(num3);
		
		System.out.print("long = ");
		System.out.println(num4);
		
		System.out.print("float = ");
		System.out.println(num5);
		
		System.out.print("double = ");
		System.out.println(num6);
		
		System.out.print("char = ");
		System.out.println(Hashtag);
		
		System.out.print("boolean = ");
		System.out.println(IamDoingJavaAssignment);
		
	

	}

}
